inline fun getResource(r: String) = File(object {}.javaClass.getResource("/").toURI())
    .parentFile.parentFile.parentFile.parentFile.toPath().resolve("processedResources/jvm/main").resolve(r).toFile()
        .readBytes()


class EscPosConverterTest : StringSpec({
    "should display image from base64" {

//     let escpos = base64::decode("").unwrap();
//
//     escpos2jpg(&escpos, 42, 1174);
        // Your base64 encoded image string
        val base64ImageString = ""

        // Decode the base64 string to get image bytes
        val escPos = Base64.getDecoder().decode(base64ImageString)
        val cl = this::class.java.getResource("").toURI();
        val pew = object {}.javaClass.getResource("/").toURI()
        val s = File(pew!!)
        val fontRegular = createFont( getResource("SourceCodePro.ttf"), 12F)
        val fontBold = createFont( getResource("SourceCodePro-Bold.ttf"), 12F)
        // Use your ESC2BMP class's parse method to get the image
        val pew2 = QRCodeGraphicsFactory().newGraphicsSquare(42).availableFormats()
        println(pew2)
        val image = EscPosConverter.Companion.escpos2bitmap(escPos, EscPosConfig(1174, fontRegular, fontBold, FontScale(14,14)))
            //.parse(imageBytes)
        val latch = CountDownLatch(1)
        // Display the image in a JFrame
        SwingUtilities.invokeLater {
            val frame = JFrame("Test Image")
            frame.defaultCloseOperation = JFrame.EXIT_ON_CLOSE
            frame.addWindowListener(object : WindowAdapter() {
                override fun windowClosed(e: WindowEvent) {
                    latch.countDown()
                }
            })
            val icon = ImageIcon(image)
            val label = JLabel(icon)
            frame.add(label)
            frame.pack()
            frame.isVisible = true
        }

        // Since this is more of a manual test, there's no automatic assertion
        // Add any necessary assertions for automated testing or simply view the window that pops up
        latch.await() // Blocks here until windowClosed event occurs
        println("Window closed, continuing execution.")
    }
})
